INSERT INTO problem VALUES('미적분','벡터 공간 기하학',1,'\\begin{align}&좌표공간의\\ 세\\ 벡터\\ a,\\ b,\\ c에\\ 대하여\\ \\det(a, b, c) = 3 일\\ 때\\ 다음을\\ 구하시오.\\\\&(a)\\quad a \\cdot (2a+b) \\times (3b + 5c)\\\\&(b)\\quad (2a\\times b) \\cdot \\{ (b \\times c) \\times (c \\times 2a)\\}\\end{align}',NULL,'\\begin{align} (a)\\quad &\\vec{a} \\cdot (2\\vec{a}+\\vec{b}) \\times (3\\vec{b} + 5\\vec{c})\\\\&= \\vec{a} \\cdot (6\\vec{a} \\times \\vec{b} + 10\\vec{a} \\times \\vec{c} + 5\\vec{b} \\times \\vec{c})\\\\&= 5(\\vec{a} \\cdot \\vec{b} \\times \\vec{c})\\\\&= 5det(\\vec{a}, \\vec{b},\\vec{c})\\\\&= 15\\\\(b)\\quad &({2\\vec{a} \\over \\vec{w}} \\times {\\vec{b} \\over \\vec{x}} ) \\cdot \\{ ({\\vec{b} \\times \\vec{c} \\over \\vec{y}}) \\times {(\\vec{c} \\times 2\\vec{a}) \\over \\vec{z}  } \\}\\\\&\\vec{w} \\cdot \\vec{y} = 2\\det(\\vec{a}, \\vec{b}, \\vec{c}) = 6\\\\&\\vec{x} \\cdot \\vec{z} = 2\\det(\\vec{b}, \\vec{c}, \\vec{a}) = 6\\\\&\\vec{w} \\cdot \\vec{z} = \\vec{x} \\cdot \\vec{y} = 0\\\\&\\therefore (\\vec{w} \\times \\vec{x}) \\cdot (\\vec{y} \\times \\vec{z}) = (\\vec{w} \\cdot \\vec{y})(\\vec{x} \\cdot \\vec{z}) - (\\vec{w} \\cdot \\vec{y})(\\vec{x} \\cdot \\vec{z}) = 36\\end{align}','\\begin{align}&(a)\\ 15\\\\&(b)\\ 36\\end{align}',NULL);
INSERT INTO problem VALUES('미적분','도함수',1,'\\begin{align}&극좌표계로\\ 다음과\\ 같이\\ 주어진\\ 곡선의\\ 개형을\\ 그리시오.\\\\&(a)\\quad r = 1 + 2 \\sin\\theta\\\\&(b)\\quad r^{2} = \\sin(3\\theta + {\\pi \\over 2})\\end{align}',NULL,NULL,'Images\\OpenSourceMathAnswer.jpg','Images\\OpenSourceMathAnswer.jpg');
INSERT INTO problem VALUES('미적분','역함수',1,'\\begin{align} &y=\\sinh x의\\ 역함수를\\ x = \\sinh^{-1}y라\\ 하자.\\ 다음\\ 물음에\\ 답하시오.\\\\&(a)\\quad 함수\\ h(y) = {d\\sinh^{-1}y \\over dy} 를\\ y에\\ 관한\\ 식으로\\ 나타내시오.\\\\&(b)\\quad f(x) = (x + X^{2})\\sinh^{-1}(x-x^{2})의\\ 원점에서의\\ 5차\\ 근사다항식을\\ 구하시오.\\end{align}',NULL,'\\begin{align} (a)\\quad &g(x) = \\sinh x라\\ 하면\\ g\\\'(x) = \\cosh x > 0 이므로\\\\&역함수정리에\\ 의해\\\\&h\'(y) = {1 \\over g\'\\cdot h(y)} = {1 \\over \\cosh(\\sinh^{-1}y)}\\\\&\\quad \\quad = {1 \\over \\sqrt{1+\\sinh^{2}(\\sinh^{-1}y)}} = {1 \\over \\sqrt{1+y^{2}}}\\\\(b)\\quad &\\sinh^{-1}y = y - {y^{3} \\over 6} + o(y^{4})\\\\&(x+x^{2}) \\sinh^{-1}(x-x2) =(x+x^{2}) \\left( (x-x^{2}) - {(x-x^{2})^{3} \\over 6} + o(x^{4})\\right)\\\\&=x^{2} - x^{4}-{1 \\over 6}\\cdot (x+x^{2})(x^{3}-3x^{4}) + o(X^{5})\\\\&=x^{2}-{7 \\over 6}x^{4} + {1 \\over 3}x^{5}+o(x^{5})\\\\&근사다항식의\\ 유일성에\\ 의해\\\\&\\ T_{5}(x) = x^{2}-{7 \\over 6}x^{4} + {1 \\over 3}x^{5} \\end{align}','\\begin{align}&(a)\\ {1 \\over \\sqrt{1+y^{2}}}\\\\&(b)\\ T_{5}(x) = x^{2}-{7 \\over 6}x^{4} + {1 \\over 3}x^{5} \\end{align}',NULL);
INSERT INTO problem VALUES('미적분','적분',1,'\\begin{align} &좌표평면\\ 위에\\ 있는\\ 사이클로이드\\ 곡선 X(t) = (t - \\sin t, 1 - \\cos t), (0 \\le t \\le 2\\pi)과\\\\&좌표평면\\ 위에서\\ 정의된\\ 함수\\ f(x,y) = x에\\ 대하여\\ 선적분 \\int_{X}f ds를\\ 구하시오. \\end{align}',NULL,'\\begin{align} &사이클로이드\\ 곡선의\\ 길이는\\ 8\\\\&{1 \\over 8}\\int_{X}xds = \\pi\\\\&\\therefore \\int_{X}fds = 8\\pi\\end{align}','\\begin{align}&\\ 8\\pi \\end{align}',NULL);
INSERT INTO problem VALUES('미적분','적분',2,'\\begin{align} &입자가\\ 원점으로부터\\ x피트의\\ 거리에\\ 있을\\ 때,\\ 이에\\ x^{2}+2x파운드의\\ 힘을\\ 가하고\\ 있다.\\\\ &이를\\ x=1부터\\ x=3까지\\ 움직이는\\ 데\\ 한\\ 일은\\ 얼마인가? \\end{align}',NULL,'\\begin{align}&\\ W=\\int_{1}^{3}(x^{2}+2x)dx = \\left.{x^{3} \\over 3}+x^{2} \\right]_{1}^{3} = {50 \\over 3}\\end{align}','\\begin{align} {50 \\over 3}ft-lb\\end{align}',NULL);
